import { BehaviorSubject } from 'rxjs/BehaviorSubject';
export class InFaxDatabase {
  constructor(aInFax) {
    this.aInFax = aInFax;
    this.dataChange = new BehaviorSubject([]);
    const infaxData = aInFax.slice();
    this.dataChange.next(infaxData);
  }

  get data() {
    return this.dataChange.value;
  }

}
