import { BehaviorSubject } from 'rxjs/BehaviorSubject';
export class UserDatabase {
  constructor(aUser) {
    this.aUser = aUser;
    this.dataChange = new BehaviorSubject([]);
    const userData = aUser.slice();
    this.dataChange.next(userData);
  }

  get data() {
    return this.dataChange.value;
  }

}
