import { BehaviorSubject } from 'rxjs/BehaviorSubject';
export class DIDDatabase {
  constructor(aDID) {
    this.aDID = aDID;
    this.dataChange = new BehaviorSubject([]);
    const didData = aDID.slice();
    this.dataChange.next(didData);
  }

  get data() {
    return this.dataChange.value;
  }

}
