import { BehaviorSubject } from 'rxjs/BehaviorSubject';
export class GroupDatabase {
  constructor(aGroup) {
    this.aGroup = aGroup;
    this.dataChange = new BehaviorSubject([]);
    const groupData = aGroup.slice();
    this.dataChange.next(groupData);
  }

  get data() {
    return this.dataChange.value;
  }

}
